INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152204, 'Tinidazole', 'Pentobarbital', 'Moderate', 'Coadministration with a barbiturate may significantly decrease the plasma concentrations and pharmacologic effects of nitroimidazoles. The proposed mechanism is barbiturate induction of nitroimidazole metabolism via hepatic microsomal enzymes.', 'DDInter', 'Patients treated concomitantly with a barbiturate may require a higher dosage of metronidazole to achieve a cure. Two- to threefold dosage increases are not uncommon. Pharmacologic response to metronidazole should be monitored and the dosage adjusted as necessary. Given its structural similarities to metronidazole, the same precaution may be applicable during therapy with tinidazole, although clinical data are lacking.', 'Metabolism', 'Patients treated concomitantly with a barbiturate may require a higher dosage of metronidazole to achieve a cure.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152367/', '', 'Suvorexant, Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, More', 'Tetracycline, Fluconazole, Cefepime, Norfloxacin, Ciprofloxacin, Cefuroxime, Secnidazole, Cefixime, Azithromycin, Amikacin, Levofloxacin, More', 1767369485);
