INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152499, 'Etonogestrel', 'Perampanel', 'Moderate', 'RECOMMENDED: Coadministration with certain anticonvulsants, such as rufinamide and perampanel, may reduce the efficacy of contraceptive hormones. The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity by the anticonvulsants.', 'DDInter', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants. In general, alternative or additional methods of birth control should be used during and for at least two weeks after short-term and 4 weeks after long-term (greater than 4 weeks) anticonvulsant therapy. Intrauterine systems are unlikely to be significantly affected because of their local action. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152662/', '', 'Ulipristal, Megestrol acetate, Estradiol, Medroxyprogesterone acetate, Estetrol', 'Ezogabine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Ganaxolone, Vigabatrin, Mephenytoin, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152500, 'Etravirine', 'Perampanel', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of perampanel, which is primarily metabolized by the isoenzyme. The interaction has been reported with known CYP450 3A4 inducers such as carbamazepine, oxcarbazepine, and phenytoin.', 'DDInter', 'According to the manufacturer, concomitant use of perampanel with strong CYP450 3A4 inducers should be avoided.', 'Metabolism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/152663/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 'Ethotoin, Gabapentin, Rufinamide, Cannabidiol, Topiramate, Ezogabine, Paramethadione, Methsuximide, Fenfluramine, Phensuximide, Lamotrigine, More', 1767369485);
