INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152885, 'Rasagiline', 'Perindopril', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may potentiate the hypotensive effect of some medications. MAOIs alone quite commonly produce orthostatic hypotension. This effect may stem from a gradual MAOI-induced accumulation of false neurotransmitters in peripheral adrenergic neurons that have minimal activity at alpha- and beta-adrenergic receptors, resulting in a functional block of sympathetic neurotransmission. The interaction has been reported with the concomitant use of beta-blockers.', 'DDInter', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment. Close monitoring for development of hypotension is recommended. Ambulatory patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution is advised during coadministration of MAOIs and other medications with hypotensive effects, especially during the first few weeks of treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153048/', '', 'Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Fenofibrate', 'Opicapone, Rotigotine, Tolcapone, Entacapone, Bromocriptine, Pramipexole, Amantadine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (152886, 'Riociguat', 'Perindopril', 'Moderate', 'Riociguat may potentiate the action of antihypertensive agents and other vasodilators. Symptomatic hypotension may occur. Riociguat stimulates soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cyclic guanosine monophosphate (cGMP). The resultant accumulation of cGMP in vascular smooth muscle cells leads to peripheral vasodilation and blood pressure decreases.', 'DDInter', 'Caution is advised if riociguat is prescribed in combination with antihypertensive agents or other vasodilators.', 'Synergism', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153049/', '', 'Atorvastatin, Simvastatin, Lovastatin, Rosuvastatin, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Fenofibrate', 'Ambrisentan, Macitentan, Metyrosine', 1767369485);
