INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153193, 'Procarbazine', 'Phendimetrazine', 'Major', 'Centrally-acting sympathomimetic agents (i.e., CNS stimulants), particularly the amphetamines and amphetamine derivatives, may precipitate severe hypertensive reactions and hyperpyrexia in patients treated with monoamine oxidase inhibitors (MAOIs). MAOIs also slow amphetamine metabolism, which may potentiate amphetamine effect on the release of norepinephrine and other monoamines from adrenergic nerve endings.', 'DDInter', 'In general, CNS stimulants should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'In general, CNS stimulants should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153356/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, More', '', 1767369485);
