INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153198, 'Rasagiline', 'Phendimetrazine', 'Moderate', 'Coadministration of nonselective monoamine oxidase (MAO) inhibitors and sympathomimetic amines has been associated with severe hypertensive reactions. The mechanism involves a synergistic sympathomimetic effect due to enhanced norepinephrine storage in adrenergic neurons (MAOI activity) and increased liberation or decreased reuptake of catecholamines (sympathomimetic activity).', 'DDInter', 'Caution is advised if rasagiline is used in combination with sympathomimetic amines. Patients with preexisting hypertension may require more frequent monitoring of their blood pressure.', 'Synergism', 'Caution is advised if rasagiline is used in combination with sympathomimetic amines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153361/', '', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Entacapone, Tolcapone, Rotigotine, Bromocriptine, Pergolide, Pramipexole, Amantadine, Levodopa', '', 1767369485);
