INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153245, 'Cyclizine', 'Phenelzine', 'Moderate', 'Coadministration of monoamine oxidase inhibitors (MAOIs) and antihistamines may result in additive central nervous system depressant effects. In addition, limited data suggest that MAOIs may potentiate and prolong the anticholinergic effects of antihistamines due to inhibition of catecholamine degradation, which may lead to overstimulation of the sympathetic nervous system.', 'DDInter', 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, procarbazine) should generally be avoided. If concomitant treatment is unavoidable, patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities.', 'Synergism', 'Prescribing antihistamines in combination with MAOIs or other agents that possess MAOI activity (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153408/', '', 'Vilazodone, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Bupropion', 'Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153246, 'Cyproheptadine', 'Phenelzine', 'Moderate', 'Cyproheptadine may antagonize the therapeutic effect of monoamine oxidase inhibitors (MAOIs). This interaction was reported in one patient treated with phenelzine for chronic depression. Another patient developed visual hallucinations following the addition of cyproheptadine to a stabilized regimen of phenelzine. Causality has not been definitely established. The mechanism is unknown but may be related to the antiserotonergic activity of cyproheptadine. MAOIs may also potentiate the sedative and anticholinergic effects of cyproheptadine.', 'DDInter', 'Patients should be monitored for potentially diminished therapeutic response to MAOIs and increased antihistaminic effects of cyproheptadine during coadministration. Discontinuation of cyproheptadine may be necessary if an interaction is suspected.', 'Antagonism', 'Patients should be monitored for potentially diminished therapeutic response to MAOIs and increased antihistaminic effects of cyproheptadine during coadministration.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153409/', '', 'Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', 'Bupropion, Viloxazine', 1767369485);
