INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153397, 'Sevoflurane', 'Phenelzine', 'Major', 'Anecdotal reports have implicated the concomitant use of general anesthesia and nonselective monoamine oxidase inhibitors (MAOIs) as causing hypotension or hypertension. Analysis of these cases suggests that other contributing factors may have been involved (e.g., meperidine and sympathomimetic agents).', 'DDInter', 'The makers of MAOIs, both selective and nonselective, consider general anesthesia to be contraindicated in patients treated with MAOIs and recommend discontinuing MAOI therapy 10 to 14 days before elective surgery.', 'Others', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153560/', '', 'Duloxetine, Vilazodone, Desvenlafaxine, Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Bupropion, Fluvoxamine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153398, 'Sibutramine', 'Phenelzine', 'Major', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs) may potentiate the pharmacologic activity of serotonergic agents such as serotonin reuptake inhibitors, 5-HT1 receptor agonists, ergot alkaloids, buspirone, dextromethorphan, and most antidepressants. The result may be an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with serotonergic agents.', 'Synergism', 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153561/', '', 'Esketamine', 'Orlistat, Naltrexone', 1767369485);
