INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153555, 'Acetohexamide', 'Phenytoin', 'Moderate', 'The coadministration with phenytoin may diminish the hypoglycemic effect of sulfonylureas. Phenytoin has been reported to cause hyperglycemia, hypoinsulinemia and glucose intolerance, thus it may interfere with blood glucose control. Conversely, some sulfonylureas may enhance the plasma concentrations and pharmacologic effects of phenytoin by competitive inhibition of its metabolism by CYP450 2C9 and/or 2C19 isoenzymes. Phenytoin intoxication has been reported during coadministration with tolbutamide specifically.', 'DDInter', 'Consideration should be given to more closely monitoring blood glucose and phenytoin levels during coadministration with sulfonylureas. Patients should be advised to notify their physician if loss of glycemic control occurs or if they experience symptoms of possible phenytoin toxicity, including drowsiness, visual disturbances, change in mental status, seizures, nausea, and ataxia.', 'Metabolism, Antagonism', 'Consideration should be given to more closely monitoring blood glucose and phenytoin levels during coadministration with sulfonylureas.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153718/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Guar gum, Dapagliflozin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153556, 'Aluminum hydroxide', 'Phenytoin', 'Moderate', 'INTERVAL: Concurrent administration of antacids may decrease the bioavailability of phenytoin. The mechanism of interaction is unknown. However, some studies have failed to find a significant interaction with these antacids.', 'DDInter', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of phenytoin, patients requiring concomitant antacid therapy should consider separating the times of administration by at least two to three hours if possible. Serum phenytoin levels and seizure activity should be monitored.', 'Absorption', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of phenytoin, patients requiring concomitant antacid therapy should consider separating the times of administration by at least two to three hours if possible.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153719/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153557, 'Aminophylline', 'Phenytoin', 'Moderate', 'Phenytoin has been reported to increase the clearance of theophylline by 40% to 50%, presumably by enhancing the hepatic CYP450 metabolism of theophylline. Also, coadministration of theophylline has been reported to lower serum phenytoin concentrations. Other hydantoins and hepatically metabolized methylxanthines may also interact.', 'DDInter', 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary. Patients should be advised to report loss of seizure control or worsening of respiratory symptoms to their physician.', 'Metabolism', 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153720/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Pentoxifylline, Zafirlukast, Bromotheophylline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153558, 'Amoxapine', 'Phenytoin', 'Moderate', 'Tricyclic antidepressants may increase serum phenytoin levels. In vitro studies suggest the mechanism may involve inhibition of CYP450 2C19-catalyzed phenytoin p-hydroxylation by tricyclic antidepressants, especially imipramine and amitriptyline. Phenytoin may induce the hepatic metabolism of desipramine resulting in decreased plasma concentrations.', 'DDInter', 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended. Patients should be advised to notify their doctor if they experience loss of seizure control or symptoms of phenytoin toxicity (drowsiness, visual disturbances, change in mental status, nausea, or ataxia). Phenytoin dosage may need to be adjusted.', 'Metabolism', 'Clinical monitoring of patient response, tolerance, and serum phenytoin concentrations is recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153721/', '', 'Lacosamide', 'Nefazodone, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Levomilnacipran', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153559, 'Amprenavir', 'Phenytoin', 'Moderate', 'In the absence of ritonavir as a pharmacokinetic booster, coadministration of amprenavir or fosamprenavir with phenytoin may result in decreased plasma concentrations of amprenavir. The proposed mechanism is phenytoin induction of amprenavir metabolism via CYP450 3A4. In contrast, when ritonavir is present, phenytoin levels may be decreased due to induction of CYP450 2C9 metabolism by ritonavir.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, amprenavir and fosamprenavir should be used cautiously with phenytoin if ritonavir is not coadministered as a boosting agent. Antiretroviral response should be monitored more closely whenever phenytoin is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary. However, if ritonavir is also prescribed, the potential for reduced therapeutic effects of phenytoin should be considered. Phenytoin dosage may need to be increased if patient demonstrates subtherapeutic phenytoin levels or experiences loss of seizure control. No change in the dosages of fosamprenavir (or amprenavir) and ritonavir is necessary during coadministration with phenytoin.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, amprenavir and fosamprenavir should be used cautiously with phenytoin if ritonavir is not coadministered as a boosting agent.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153722/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Levetiracetam, Ethotoin, Rufinamide, Paramethadione, Perampanel, Vigabatrin, Mephenytoin, More', 'Paritaprevir, Zanamivir, Velpatasvir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153560, 'Anisindione', 'Phenytoin', 'Moderate', 'Some oral anticoagulants have been reported to increase phenytoin half-life and serum concentrations, possibly by inhibiting CYP450 metabolism. The addition of phenytoin to warfarin therapy has initially increased prothrombin time, but thereafter reduced anticoagulant effects. The mechanism is not known, but may be due to initial CYP450 metabolic inhibition, followed by isoenzyme induction.', 'DDInter', 'Phenytoin serum concentrations and INR/PT should be monitored in patients receiving this combination and whenever the dosage is changed or discontinued. Patients should be advised to promptly report any signs of bleeding (e.g., pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, nosebleeds, bleeding of gums from brushing, red or brown urine, or red or black stools) or hydantoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia) to their physician.', 'Metabolism', 'Phenytoin serum concentrations and INR/PT should be monitored in patients receiving this combination and whenever the dosage is changed or discontinued.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153723/', '', 'Brivaracetam, Ezogabine, Cenobamate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, Clonazepam, More', '', 1767369485);
