INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153562, 'Phenytoin', 'Artesunate', 'Moderate', 'Coadministration with potent inducers of uridine diphosphate glucuronosyltransferase (UGT) may decrease the peak plasma concentration (Cmax) and systemic exposure (AUC) of dihydroartemisinin (DHA), an active metabolite of artesunate and substrate of UGT 1A9 or 2B7. Reduced efficacy of artesunate may occur.', 'DDInter', 'The potential for diminished pharmacologic effects of artesunate should be considered during coadministration with potent UGT inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of artesunate should be considered during coadministration with potent UGT inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153725/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, More', 'Lumefantrine, Pyrimethamine, Tafenoquine, Atovaquone, Halofantrine, Primaquine, Proguanil', 1767369485);
