INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153639, 'Flurazepam', 'Phenytoin', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', 'DDInter', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153802/', '', 'Lacosamide', 'Thiopental', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153640, 'Fluvoxamine', 'Phenytoin', 'Moderate', 'Coadministration with fluvoxamine may increase the serum concentrations of phenytoin. The proposed mechanism is fluvoxamine inhibition of CYP450 2C9 and 2C19, the isoenzymes responsible for the metabolic clearance of phenytoin.', 'DDInter', 'Caution is advised if phenytoin must be used with fluvoxamine. Pharmacologic response and serum phenytoin levels should be monitored more closely whenever fluvoxamine is added to or withdrawn from therapy, and the phenytoin dosage adjusted as necessary. The same precaution may be applicable during therapy with other hydantoins, although clinical data are lacking. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status.', 'Metabolism', 'Caution is advised if phenytoin must be used with fluvoxamine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153803/', '', 'Nefazodone, Oxitriptan, Viloxazine, Milnacipran, Tryptophan, Levomilnacipran', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153641, 'Fosamprenavir', 'Phenytoin', 'Moderate', 'In the absence of ritonavir as a pharmacokinetic booster, coadministration of amprenavir or fosamprenavir with phenytoin may result in decreased plasma concentrations of amprenavir. The proposed mechanism is phenytoin induction of amprenavir metabolism via CYP450 3A4. In contrast, when ritonavir is present, phenytoin levels may be decreased due to induction of CYP450 2C9 metabolism by ritonavir.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, amprenavir and fosamprenavir should be used cautiously with phenytoin if ritonavir is not coadministered as a boosting agent. Antiretroviral response should be monitored more closely whenever phenytoin is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary. However, if ritonavir is also prescribed, the potential for reduced therapeutic effects of phenytoin should be considered. Phenytoin dosage may need to be increased if patient demonstrates subtherapeutic phenytoin levels or experiences loss of seizure control. No change in the dosages of fosamprenavir (or amprenavir) and ritonavir is necessary during coadministration with phenytoin.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, amprenavir and fosamprenavir should be used cautiously with phenytoin if ritonavir is not coadministered as a boosting agent.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153804/', '', 'Ethotoin, Brivaracetam, Rufinamide, Valproic acid, Topiramate, Ezogabine, Paramethadione, Perampanel, Mephenytoin, Methsuximide, Lamotrigine, More', 'Paritaprevir, Zanamivir, Velpatasvir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153642, 'Fosaprepitant', 'Phenytoin', 'Moderate', 'Theoretically, coadministration of phenytoin and aprepitant may result in decreased plasma concentrations of both drugs. The mechanism is phenytoin induction of CYP450 3A4, of which aprepitant is a substrate, and aprepitant induction of CYP450 2C9, of which phenytoin is a substrate.', 'DDInter', 'During coadministration of aprepitant and phenytoin, the possibility of a diminished therapeutic response to both drugs should be considered. Pharmacologic response should be monitored more closely whenever one or the other drug is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'During coadministration of aprepitant and phenytoin, the possibility of a diminished therapeutic response to both drugs should be considered.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153805/', '', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Lacosamide, Levetiracetam, Ethotoin, Rufinamide, Paramethadione, Perampanel, Vigabatrin, More', '', 1767369485);
