INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153681, 'Phenytoin', 'Levomefolic acid', 'Moderate', 'Anticonvulsants including, but not limited to, carbamazepine, lamotrigine, phenobarbital, phenytoin, primidone, and valproic acid have been shown to impair folate absorption and increase the metabolism of circulating folate. Coadministration with folate therapy may reduce the anticonvulsant effects of phenytoin, phenobarbital, primidone, and succinimides. The exact mechanism of interaction is unknown. Available data pertain primarily to phenytoin.', 'DDInter', 'Caution is advised when folate therapy is coadministered with anticonvulsants. Close monitoring for clinical and laboratory evidence of diminished therapeutic response to both treatments is recommended. Patients should be advised to notify their physician if they experience loss of seizure control.', 'Antagonism', 'Caution is advised when folate therapy is coadministered with anticonvulsants.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153845/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lacosamide, Levetiracetam, Tiagabine, Clonazepam, Paramethadione, Perampanel, Ganaxolone, More', '', 1767369485);
