INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153701, 'Phenytoin', 'Norgestrel', 'Major', 'RECOMMENDED: Coadministration with certain anticonvulsants such as carbamazepine, eslicarbazepine, oxcarbazepine, phenobarbital, phenytoin, and primidone may reduce the efficacy of contraceptive hormones. There have been numerous case reports of menstrual abnormalities (e.g., breakthrough bleeding, amenorrhea, irregular menses) and unintended pregnancy occurring in women who received oral contraceptives with anticonvulsants. The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and hormone-binding globulin capacity by some anticonvulsants.', 'DDInter', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153865/', '', 'Ezogabine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Ganaxolone, Vigabatrin, Mephenytoin, More', 'Estetrol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153702, 'Phenytoin', 'Opium', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153866/', '', 'Lacosamide', 'Eluxadoline, Diphenoxylate, Difenoxin, Naloxone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153703, 'Phenytoin', 'Oxazepam', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', 'DDInter', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153867/', '', 'Lacosamide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153704, 'Phenytoin', 'Oxtriphylline', 'Moderate', 'Phenytoin has been reported to increase the clearance of theophylline by 40% to 50%, presumably by enhancing the hepatic CYP450 metabolism of theophylline. Also, coadministration of theophylline has been reported to lower serum phenytoin concentrations. Other hydantoins and hepatically metabolized methylxanthines may also interact.', 'DDInter', 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary. Patients should be advised to report loss of seizure control or worsening of respiratory symptoms to their physician.', 'Metabolism', 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153868/', '', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Omalizumab, Tezepelumab, Mercaptopurine, Benralizumab, Caffeine, Reslizumab, Dyphylline, Mepolizumab, Pentoxifylline, Zafirlukast, Bromotheophylline', 1767369485);
