INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153721, 'Posaconazole', 'Phenytoin', 'Moderate', 'Phenytoin and other enzyme-inducing anticonvulsants may decrease the plasma concentration of posaconazole. The mechanism is induction of posaconazole clearance via the UDP-glucuronosyltransferase glucuronidation and P-glycoprotein efflux pathways.', 'DDInter', 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk. If coadministration is necessary, patient response to the antifungal agent must be closely monitored. If phenytoin is used, pharmacologic response and serum phenytoin levels should also be monitored closely following initiation or discontinuation of posaconazole, and the phenytoin dosage adjusted as necessary.', 'Metabolism', 'Use of posaconazole in combination with enzyme-inducing anticonvulsants should generally be avoided unless the potential benefit outweighs the risk.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153885/', '', 'Brivaracetam, Topiramate, Cenobamate, Lamotrigine, Methylphenobarbital, Levetiracetam, Oxcarbazepine, Ethotoin, Rufinamide, Paramethadione, Fosphenytoin, More', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 1767369485);
