INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (153812, 'Telaprevir', 'Phenytoin', 'Major', 'Coadministration with certain anticonvulsants may significantly decrease the plasma concentrations of telaprevir. The mechanism involves induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of telaprevir and other protease inhibitors. Anticonvulsants that are considered potent inducers of CYP450 3A4 include carbamazepine, phenobarbital, and phenytoin.', 'DDInter', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic telaprevir levels, concomitant use with carbamazepine, phenobarbital, or phenytoin is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/153976/', '', 'Ethotoin, Brivaracetam, Gabapentin, Valproic acid, Topiramate, Ezogabine, Paramethadione, Mephenytoin, Methsuximide, Fenfluramine, Lamotrigine, More', 'Paritaprevir, Zanamivir, Velpatasvir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, Tecovirimat, Pibrentasvir, Rimantadine, Valganciclovir, More', 1767369485);
