INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154356, 'Diflunisal', 'Pindolol', 'Minor', 'High doses of salicylates may blunt the antihypertensive effects of beta-blockers. The proposed mechanism is inhibition of prostaglandin synthesis. Low-dose aspirin does not appear to affect blood pressure. In addition, beta-blockers may exert an antiplatelet effect, which may be additive with the effects of some salicylates. Metoprolol may also increase aspirin absorption and/or plasma concentrations of salicylates; however, the clinical significance of this effect is unknown. Data have been conflicting. Until more information is available, patients who require concomitant therapy should be monitored for altered antihypertensive response whenever a salicylate is introduced or discontinued, or when its dosage is modified.', 'DDInter', '-', 'Antagonism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154524/', '', 'Ziconotide', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154357, 'Digitoxin', 'Pindolol', 'Moderate', 'Concomitant use of digitalis glycosides and beta-blockers may increase the risk of bradycardia. These agents slow atrioventricular conduction and decrease heart rate, hence they may have additive cardiac effects during coadministration. Some beta-blockers such as carvedilol, esmolol, and talinolol have also been reported to increase the systemic bioavailability of digoxin. The mechanism may involve enhanced absorption as well as reduced renal excretion of digoxin due to inhibition of intestinal and renal P-glycoprotein efflux transporter.', 'DDInter', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers. Serum digitalis levels, heart rate, and blood pressure should be monitored closely, particularly during the first few weeks of concomitant therapy. Patients should be advised to notify their physician if they experience anorexia, nausea, visual changes, irregular heartbeat, slow pulse, dizziness, or syncope. Beta-blockers should not be used in patients with overt or decompensated congestive heart failure, as sympathetic stimulation may be a vital component in maintaining hemodynamic function in these patients and its inhibition by beta blockade may worsen the heart failure.', 'Excretion', 'Caution is advised during coadministration of digitalis glycosides and beta-blockers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154525/', '', 'Carteolol', '', 1767369485);
