INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154437, 'Pindolol', 'Methacholine', 'Major', 'Patients who are concurrently receiving beta blockers may have exaggerated or prolonged responses (i.e., bronchoconstriction) to methacholine challenge and may not respond readily to treatment. Beta-2-adrenergic blockade increases airway resistance. Ophthalmically applied beta blockers undergo some systemic absorption and potentially may also interact.', 'DDInter', 'Administration of methacholine to a patient on any beta blocker therapy is considered contraindicated.', 'Others', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154606/', '', 'Nebivolol, Carteolol', 'Methylene blue, Hexaminolevulinate, Secretin human, Sermorelin, Sorbitol, Galactose, Magnesium sulfate, Vitamin A, Caffeine, Metyrapone, Edrophonium, More', 1767369485);
