INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154441, 'Pindolol', 'Methoxyflurane', 'Moderate', 'The antihypertensive effects of methoxyflurane and beta blockers may be additive. The combination may cause profound hypotension. Older anesthetics such as methoxyflurane have more severe circulatory depressant effects in the presence of beta-blockade.', 'DDInter', 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery. However, in general, patients taking beta blockers should not be anaesthetized with methoxyflurane. Instead, an anesthetic with minimal negative inotropic activity may be considered. If methoxyflurane must be used, beta blocker therapy should be withdrawn gradually and generally completed 24 to 48 hours before anesthesia.', 'Synergism', 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154610/', '', 'Carteolol', 'Ziconotide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154442, 'Pindolol', 'Methysergide', 'Moderate', 'Several case reports have suggested that beta-blockers may potentiate the vasoconstrictive action of ergot alkaloids. The exact mechanism of interaction is unknown, but may involve blockade of beta-2-mediated (i.e., sympathetic) vasodilatation. In addition, beta-1 blockade reduces cardiac output, which can diminish blood flow and exacerbate ergot-induced vasospasm.', 'DDInter', 'Except for increased awareness of the interaction, no specific intervention appears to be necessary. Patients should be advised to seek immediate medical attention if they experience potential symptoms of ischemia such as coldness, pallor, cyanosis, numbness, tingling, or pain in the extremities; muscle weakness; severe or worsening headache; visual disturbances; severe abdominal pain; chest pain; and shortness of breath.', 'Synergism', 'Except for increased awareness of the interaction, no specific intervention appears to be necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154611/', '', 'Frovatriptan, Rimegepant, Eptinezumab, Atogepant, Eletriptan, Rizatriptan, Ubrogepant, Naratriptan, Galcanezumab, Sumatriptan, Erenumab, More', '', 1767369485);
