INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154499, 'Tadalafil', 'Pindolol', 'Minor', 'Based on their pharmacology, phosphodiesterase-5 (PDE5) inhibitors may conceivably potentiate the hypotensive effect of antihypertensive medications. These agents inhibit PDE5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/154668/', '', 'Fesoterodine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, Ammonium chloride, More', '', 1767369485);
