INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154904, 'Pitolisant', 'Methotrimeprazine', 'Major', 'Coadministration with strong inhibitors of CYP450 2D6 may increase plasma concentrations of pitolisant. The proposed mechanism is decreased clearance of pitolisant due to inhibition of its metabolism via this isoenzyme.', 'DDInter', 'A dosage reduction is recommended for pitolisant during coadministration with strong inhibitors of CYP450 2D6. For patients receiving a strong CYP450 2D6 inhibitor, the dose of pitolisant should be initiated at 8.9 mg once daily and increased after 7 days to a maximum dosage of 17.8 mg once daily. For patients already receiving a stable dose of pitolisant, the dose of pitolisant should be reduced by half when initiating treatment with a strong CYP450 2D6 inhibitor. Patients should be monitored for clinical response and increased adverse effects such as headache, insomnia, nausea, anxiety, increased heart rate, QT prolongation, hallucinations, abdominal pain, upper respiratory tract infections, and musculoskeletal pain.', 'Metabolism', 'A dosage reduction is recommended for pitolisant during coadministration with strong inhibitors of CYP450 2D6.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155073/', '', 'Quetiapine, Thiothixene, Loxapine, Molindone, Lurasidone', 'Dalfampridine, Inotersen, Riluzole, Tafamidis', 1767369485);
