INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154994, 'Venlafaxine', 'Pitolisant', 'Moderate', 'Coadministration with inhibitors of CYP450 2D6 may increase plasma concentrations of pitolisant. The proposed mechanism is decreased clearance of pitolisant due to inhibition of its metabolism via this isoenzyme. Clinically, high plasma levels of pitolisant may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 'Caution and clinical monitoring are recommended if pitolisant is used in combination with CYP450 2D6 inhibitors. Patients should be monitored for clinical response and increased adverse effects such as headache, insomnia, nausea, anxiety, increased heart rate, QT prolongation, hallucinations, abdominal pain, upper respiratory tract infections and musculoskeletal pain, and the dosage of pitolisant adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution and clinical monitoring are recommended if pitolisant is used in combination with CYP450 2D6 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155163/', '', 'Phenelzine, Tranylcypromine, Desvenlafaxine, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Vilazodone, Esketamine', 'Dalfampridine, Tafamidis, Riluzole', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (154995, 'Vinblastine', 'Pitolisant', 'Moderate', 'Coadministration with pitolisant may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Pitolisant is a borderline/weak inducer of CYP450 3A4.', 'DDInter', 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates. Clinical and laboratory monitoring may be appropriate whenever pitolisant is added to or withdrawn from therapy, and dosage adjustments made if necessary. It may be advisable to avoid concomitant use of pitolisant and CYP450 3A4 substrates with a narrow therapeutic index, if possible.', 'Metabolism', 'Caution is advised when pitolisant is used in combination with sensitive CYP450 3A4 substrates.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155164/', '', 'Trabectedin, Cabazitaxel, Paclitaxel, Topotecan, Teniposide, Docetaxel', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Amifampridine, Deutetrabenazine, Tafamidis', 1767369485);
