INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (155157, 'Polyethylene glycol (3350)', 'Phenolphthalein', 'Moderate', 'Concomitant use of stimulant laxatives (e.g., bisacodyl, sodium picosulfate) may increase the risk of serious gastrointestinal adverse effects associated with certain osmotic laxatives (e.g., polyethylene glycol (PEG), oral sulfate solution), such as colonic mucosal ulcerations or ischemic colitis. When administered in conjunction with an osmotic laxative such as PEG, increased intramural pressure secondary to increased peristalsis may lead to ischemic colitis and perforation.', 'DDInter', 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives. However, stimulant laxatives, in particular bisacodyl and sodium picosulfate, are sometimes used with PEG in certain bowel cleansing regimens to help reduce dose volume and improve patient tolerability and acceptance. Please consult individual product labeling for specific recommendations and guidance. Patients using osmotic bowel cleansing products and stimulant laxatives who present with sudden abdominal pain, rectal bleeding, or other symptoms of ischemic colitis should be evaluated promptly.', 'Synergism', 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155326/', '', 'Alvimopan, Sorbitol, Tegaserod, Naloxegol, Naldemedine, Methylnaltrexone, Lactitol, Sodium phosphate, monobasic, Magnesium oxide, Prucalopride, Sodium sulfate, More', '', 1767369485);
