INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (155801, 'Rucaparib', 'Posaconazole', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155970/', '', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Flucytosine, Anidulafungin', 'Pertuzumab, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Enasidenib, Belzutifan, Mitotane, Niraparib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (155802, 'Ruxolitinib', 'Posaconazole', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Indication specific dose modifications should be made when ruxolitinib is coadministered with strong CYP450 3A4 inhibitors. The recommended starting dose for patients with Polycythemia vera (PV) coadministered potent CYP450 3A4 inhibitors is ruxolitinib 5 mg twice a day. For patients with MF or PV who are stabilized on ruxolitinib 10 mg twice a day or greater and starting a potent CYP450 3A4 inhibitor, the ruxolitinib dose should be reduced by 50% (rounded up to the closest available tablet strength). For patients with MF or PV stabilized on a dose of 5 mg twice a day and starting fluconazole (at a dose of 200 mg per day or less), the ruxolitinib dose should be reduced to 5 mg once a day. For patients with MF or PV stabilized on ruxolitinib 5 mg once a day, concomitant use of strong CYP450 3A4 inhibitors should be avoided or ruxolitinib therapy interrupted for the duration of strong CYP450 3A4 inhibitor use. For patients with for acute graft versus host disease (GVHD) coadministered strong CYP450 3A4 inhibitors, the ruxolitinib dose should be reduced to 5 mg once a day with concomitant ketoconazole use; however, no dose adjustments are necessary with other potent CYP450 3A4 inhibitors.', 'Metabolism', 'Indication specific dose modifications should be made when ruxolitinib is coadministered with strong CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155971/', '', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 'Trametinib, Afatinib, Dacomitinib, Tivozanib, Alectinib', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (155803, 'Salmeterol', 'Posaconazole', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/155972/', '', 'Mometasone furoate, Umeclidinium, Aclidinium, Glycopyrronium, Epinephrine, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 'Caspofungin, Micafungin, Amphotericin B, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
