INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (156289, 'Selumetinib', 'Prasugrel', 'Moderate', 'Selumetinib capsules contain vitamin E and may potentiate the effects of anticoagulants and platelet inhibitors. Vitamin E is thought to inhibit the oxidation of reduced vitamin K and interfere with the functions of vitamin K-dependent clotting factors. These effects appear to be dose-dependent and greater in individuals with preexisting vitamin K deficiency.', 'DDInter', 'Close clinical and laboratory observation for hematologic complications may be appropriate when selumetinib is initiated in patients stabilized on anticoagulant or antiplatelet therapy. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Close clinical and laboratory observation for hematologic complications may be appropriate when selumetinib is initiated in patients stabilized on anticoagulant or antiplatelet therapy.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/156458/', '', 'Alteplase, Streptokinase, Tenecteplase, Acenocoumarol, Selexipag, Urokinase, Anistreplase, Reteplase', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Larotrectinib, Bosutinib, More', 1767369485);
