INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (156899, 'Sonidegib', 'Primidone', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of sonidegib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of sonidegib with potent and moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157068/', '', 'Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, Paraldehyde, More', 'Pertuzumab, Oxaliplatin, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tagraxofusp, Pegaspargase, Enasidenib, Belzutifan, Porfimer sodium, Aflibercept, More', 1767369485);
