INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (156923, 'Timolol', 'Primidone', 'Moderate', 'Coadministration with barbiturates may decrease the plasma concentrations and pharmacologic effects of certain beta-blockers when administered orally. The proposed mechanism is barbiturate induction of hepatic microsomal and first-pass metabolism.', 'DDInter', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days. Pharmacologic response to beta-blockers should be monitored more closely whenever a barbiturate is added to or withdrawn from therapy, and the beta-blocker dosage adjusted as necessary. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', 'Metabolism', 'Barbiturates may variously reduce the effects of certain beta-blockers when given for more than a few days.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157092/', '', 'Nadolol, Esmolol, Betaxolol, Bisoprolol, Carteolol, Sotalol, Pindolol, Neostigmine, Physostigmine, Acetylcholine, Betaxolol, More', 'Suvorexant, Nitrazepam, Triazolam, Thiopental, Eszopiclone, Ramelteon, Methohexital, Lemborexant, Estazolam, Ethchlorvynol, Melatonin, More', 1767369485);
