INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157304, 'Valbenazine', 'Procarbazine', 'Major', 'Coadministration of valbenazine with monoamine oxidase inhibitors (MAOIs) may increase the risk of adverse effects such as serotonin syndrome or reduce the therapeutic effects of valbenazine. The mechanism for this interaction has not been delineated; however, concomitant use of these drugs may increase the concentration of monoamine neurotransmitters in synapses.', 'DDInter', 'The use of valbenazine with MAOIs should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Alternative treatment may be required if an interaction is suspected.', 'Others', 'The use of valbenazine with MAOIs should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157473/', '', 'Pertuzumab, Olaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Dalfampridine, Sodium oxybate, Riluzole, Inotersen, Pitolisant, Amifampridine, Tafamidis', 1767369485);
