INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157591, 'Flutamide', 'Propafenone', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157760/', '', 'Anastrozole, Fulvestrant, Letrozole, Aminoglutethimide, Exemestane', 'Moricizine, Tocainide, Bretylium, Mexiletine', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157592, 'Fluvoxamine', 'Propafenone', 'Moderate', 'Coadministration with certain selective serotonin reuptake inhibitors (SSRIs) may increase the plasma concentrations of propafenone. The mechanism is SSRI inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of propafenone in most people.', 'DDInter', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with propafenone. Lower initial dosages of the antiarrhythmic agent may be appropriate. Patients who are already stabilized on their antiarrhythmic regimen should be monitored for altered effects on myocardial conduction following addition or discontinuation of SSRI therapy (up to 2 weeks for most SSRIs and 5 weeks for fluoxetine due to its long half-life), and the antiarrhythmic dosage adjusted if necessary. Alternatively, an antidepressant that does not interfere with CYP450 2D6 metabolism may be considered, such as citalopram, escitalopram, or venlafaxine.', 'Metabolism', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with propafenone.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157761/', '', 'Phenelzine, Tranylcypromine, Desvenlafaxine, St. John''s Wort, Isocarboxazid, Oxitriptan, Tryptophan, Vortioxetine, Milnacipran, Levomilnacipran, Vilazodone, Esketamine', 'Tocainide, Procainamide, Bretylium, Moricizine, Ibutilide', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157593, 'Fosamprenavir', 'Propafenone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of propafenone, which may result in proarrhythmic events and exaggerated beta-adrenergic blocking activity.', 'DDInter', 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors. Therapeutic response to propafenone as well as clinical and electrocardiographic evidence of toxicity should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the propafenone dosage adjusted as necessary. Patients should be monitored for potential adverse effects such as new or worsening arrhythmias or heart failure, edema, bradycardia, and atrioventricular block. Simultaneous use of propafenone with both a CYP450 3A4 inhibitor and a CYP450 2D6 inhibitor (or in patients with CYP450 2D6 deficiency) should be avoided.', 'Metabolism', 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157762/', '', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Cabotegravir, Valaciclovir, Zanamivir, Velpatasvir, Dolutegravir, Oseltamivir, More', 1767369485);
