INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157654, 'Rifampicin', 'Propafenone', 'Moderate', 'Rifampin may significantly decrease plasma concentrations of propafenone by inducing its CYP450 hepatic metabolism. Reductions in propafenone half-life, twofold increases in clearance, and reductions in bioavailability have been reported. Therapeutic failure may occur.', 'DDInter', 'The patient should be monitored for inadequate response to propafenone and subtherapeutic plasma concentrations. Maximum effects of the interaction may not be apparent for 2 weeks after rifampin is initiated or discontinued. Dosage should be adjusted as necessary. Patients should be advised to promptly notify their physician if they experience a return of their arrhythmia (i.e., syncope, palpitations, irregular heartbeats).', 'Metabolism', 'The patient should be monitored for inadequate response to propafenone and subtherapeutic plasma concentrations.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157823/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, Pyridoxine, More', 'Procainamide, Flecainide, Bretylium, Moricizine, Ibutilide', 1767369485);
