INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157825, 'Dicoumarol', 'Propranolol', 'Minor', 'Some oral beta-blockers may increase serum levels of oral anticoagulants and enhance anticoagulant effects. The mechanism is unknown . Data have been conflicting. Case reports exist for propranolol, while acebutolol, atenolol, betaxolol, bisoprolol, carvedilol, esmolol, metoprolol, and pindolol reportedly do not interact. Hypoprothrombinemic effects are expected to be minimal. It is recommended that PT or INR be monitored when a beta-blocker is added to or deleted from the medical regimen of a patient receiving an oral anticoagulant. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'DDInter', '-', 'Others', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157995/', '', 'Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Lepirudin, Urokinase, Cangrelor, Caplacizumab, Argatroban, Prasugrel, Desirudin, More', 'Felodipine, Amlodipine, Metoprolol, Carvedilol, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Atenolol, Acebutolol, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157826, 'Diflunisal', 'Propranolol', 'Minor', 'High doses of salicylates may blunt the antihypertensive effects of beta-blockers. The proposed mechanism is inhibition of prostaglandin synthesis. Low-dose aspirin does not appear to affect blood pressure. In addition, beta-blockers may exert an antiplatelet effect, which may be additive with the effects of some salicylates. Metoprolol may also increase aspirin absorption and/or plasma concentrations of salicylates; however, the clinical significance of this effect is unknown. Data have been conflicting. Until more information is available, patients who require concomitant therapy should be monitored for altered antihypertensive response whenever a salicylate is introduced or discontinued, or when its dosage is modified.', 'DDInter', '-', 'Antagonism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/157996/', '', 'Ivabradine', 'Ziconotide', 1767369485);
