INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157854, 'Galantamine', 'Propranolol', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', 'DDInter', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158025/', '', 'Ginkgo biloba, Aducanumab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157855, 'Garlic', 'Propranolol', 'Minor', 'Garlic has been found in some studies to lower blood pressure and may theoretically potentiate the effects of hypotensive agents. A meta-analysis of 11 randomized, placebo-controlled studies showed a mean decrease of 4.6 mmHg for systolic blood pressure and no significant effect for diastolic blood pressure in subjects treated with garlic (most often garlic powder 600 to 900 mg/day, providing 3.6 to 5.4 mg of the active component allicin) compared to placebo.', 'DDInter', '-', 'Synergism', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158026/', '', 'Ivabradine', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157856, 'Givosiran', 'Propranolol', 'Moderate', 'Coadministration with givosiran may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2 and/or CYP450 2D6. The proposed mechanism is decreased clearance due to givosiran-mediated inhibition of CYP450 1A2 and 2D6 isoenzymes.', 'DDInter', 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6. Concomitant use should generally be avoided with CYP450 1A2 or 2D6 substrates that have a narrow therapeutic range where minimal concentration changes may lead to serious or life-threatening toxicities. If concomitant use is unavoidable, the dosage of the CYP450 1A2 or 2D6 substrate should be reduced according to approved product labeling or clinical response and tolerance. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever givosiran is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158027/', '', 'Felodipine, Amlodipine, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Acetylsalicylic acid, Atenolol, Acebutolol, Labetalol, Nadolol, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, Agalsidase beta, More', 1767369485);
