INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157898, 'Propranolol', 'Methimazole', 'Moderate', 'The clearance of some beta-blockers with high extraction ratios may be reduced when a euthyroid state is achieved after the addition of antithyroid agents. Sotalol, nadolol, carteolol, and atenolol, which are primarily eliminated by the kidney, do not appear to be affected by changes in thyroid function.', 'DDInter', 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed. Beta blocker dose reductions may be required.', 'Others', 'The patient should be closely monitored for altered efficacy and safety while achieving a euthyroid state or when the antithyroid agent dosage is added, discontinued, or changed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158069/', '', 'Felodipine, Nebivolol, Nifedipine, Bisoprolol, Ivabradine, Sotalol, Nadolol, Esmolol, Betaxolol, Nebivolol, Bisoprolol, More', 'Potassium perchlorate', 1767369485);
