INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157900, 'Propranolol', 'Methoxyflurane', 'Moderate', 'The antihypertensive effects of methoxyflurane and beta blockers may be additive. The combination may cause profound hypotension. Older anesthetics such as methoxyflurane have more severe circulatory depressant effects in the presence of beta-blockade.', 'DDInter', 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery. However, in general, patients taking beta blockers should not be anaesthetized with methoxyflurane. Instead, an anesthetic with minimal negative inotropic activity may be considered. If methoxyflurane must be used, beta blocker therapy should be withdrawn gradually and generally completed 24 to 48 hours before anesthesia.', 'Synergism', 'It is currently recommended that beta blocker therapy not be routinely withdrawn prior to surgery.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158071/', '', 'Acetylsalicylic acid, Ivabradine, Carteolol', 'Ziconotide', 1767369485);
