INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157979, 'Vemurafenib', 'Propranolol', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 and/or 2D6 isoenzymes. The mechanism is decreased clearance due to inhibition of CYP450 1A2 and 2D6 activity by vemurafenib. The interaction has been studied with caffeine and dextromethorphan, probe substrates for CYP450 1A2 and 2D6, respectively.', 'DDInter', 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vemurafenib is added to or withdrawn from therapy. Concomitant use of vemurafenib and substrates of CYP450 1A2 and/or 2D6 with a narrow therapeutic index (e.g., antiarrhythmics; theophylline) should be avoided if possible.', 'Metabolism', 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158150/', '', 'Felodipine, Amlodipine, Nifedipine, Bisoprolol, Acetylsalicylic acid, Atenolol, Acebutolol, Labetalol, Nadolol, Esmolol, Betaxolol, More', 'Tinidazole, Simeprevir, Sulfadiazine, Lactic acid, Mitapivat, Methazolamide, Ciclopirox, Darunavir, Probenecid, Tioconazole, Brinzolamide, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157980, 'Voriconazole', 'Propranolol', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of drugs that are substrates of CYP450 2C19, 2C9, and/or 3A4. The mechanism is decreased clearance due to inhibition of those isoenzymes by voriconazole. Increased plasma levels and/or pharmacologic effects of drugs such as cyclosporine, sirolimus, tacrolimus (3A4 substrates), and warfarin (2C9 substrate) have been reported during coadministration with voriconazole.', 'DDInter', 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voriconazole is added to or withdrawn from therapy. The manufacturer specifically recommends that a dosage reduction be considered for benzodiazepines, statins, long-acting opiates, and vinca alkaloids that are metabolized by the affected isoenzymes when used with voriconazole.', 'Metabolism', 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158151/', '', 'Carvedilol, Nebivolol, Bisoprolol, Acebutolol, Nadolol, Carvedilol, Esmolol, Betaxolol, Nebivolol, Penbutolol, Bisoprolol, More', 'Micafungin, Posaconazole, Oteseconazole, Flucytosine, Anidulafungin', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157981, 'Zaleplon', 'Propranolol', 'Moderate', 'Many psychotherapeutic and CNS-active agents (e.g., anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, alcohol, muscle relaxants) exhibit hypotensive effects, especially during initiation of therapy and dose escalation. Coadministration with antihypertensives and other hypotensive agents, in particular vasodilators and alpha-blockers, may result in additive effects on blood pressure and orthostasis.', 'DDInter', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents. Avoid alcohol in patients receiving vasodilating antihypertensive drugs. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia.', 'Synergism', 'Caution and close monitoring for development of hypotension is advised during coadministration of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158152/', '', 'Felodipine, Nifedipine, Ivabradine', 'Suvorexant, Thiopental, Eszopiclone, Ramelteon, Lemborexant, Methohexital, Estazolam, Melatonin, Valerian', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157982, 'Zileuton', 'Propranolol', 'Moderate', 'Zileuton is believed to inhibit the hepatic metabolism of propranolol. Propranolol serum concentrations may increase by as much as 100% with a resultant increase in beta blockade.', 'DDInter', 'If zileuton and propranolol must be given concomitantly, the patient should be observed closely for altered propranolol effects and the dose adjusted accordingly. Patients should be advised to notify their physician if they experience syncope,slow pulse, irregular heartbeat, dizziness, or shortness of breath.', 'Metabolism', 'If zileuton and propranolol must be given concomitantly, the patient should be observed closely for altered propranolol effects and the dose adjusted accordingly.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158153/', '', 'Acetylsalicylic acid, Atenolol, Ivabradine, Sotalol, Nadolol, Esmolol, Carteolol, Sotalol, Atenolol, Nadolol, Atenolol, Sotalol', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (157983, 'Ziprasidone', 'Propranolol', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/158154/', '', 'Droperidol, Pimozide, Pimavanserin', '', 1767369485);
