INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159025, 'Riociguat', 'Quinine', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 or moderate to potent inhibitors of P-glycoprotein (P-gp) or breast cancer resistant protein (BCRP) may increase the plasma concentrations of riociguat, which is partially metabolized by CYP450 3A and also a substrate of the P-gp and BCRP efflux transporters. Increased levels of riociguat may increase the risk for hypotension.', 'DDInter', 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors. Patients should be monitored for signs and symptoms of hypotension, and the dosage of riociguat adjusted if necessary.', 'Metabolism', 'Caution is advised when riociguat is prescribed with potent CYP450 3A4 inhibitors or moderate to potent P-gp or BCRP inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159197/', '', 'Risdiplam, Collagenase clostridium histolyticum, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, More', 'Ambrisentan, Macitentan, Metyrosine, Tadalafil', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159026, 'Ripretinib', 'Quinine', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of ripretinib and its active metabolite, DP-5439. In vitro studies indicate that ripretinib and DP-5439 are substrates of the P-gp efflux transporter.', 'DDInter', 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp. Clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary. Patients should be monitored for development of adverse effects such as nausea, vomiting, constipation, diarrhea, alopecia, arthralgia, myalgia, hypertension, cardiac dysfunction, and palmar-plantar erythrodysesthesia.', 'Absorption', 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159198/', '', 'Risdiplam, Collagenase clostridium histolyticum, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, More', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Selumetinib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, Axitinib, Regorafenib, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159027, 'Ritodrine', 'Quinine', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159199/', '', 'Risdiplam, Collagenase clostridium histolyticum, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 'Bremelanotide, Flibanserin, Cabergoline, Black cohosh, Bromocriptine, Naproxen, Ibuprofen', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159028, 'Rivaroxaban', 'Quinine', 'Moderate', 'Coadministration with weak or moderate dual inhibitors of CYP450 3A4 and P-glycoprotein (P-gp) may increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', 'In patients with CrCl of 15 to less than 80 mL/min, the use of rivaroxaban with weak or moderate dual inhibitors of CYP450 3A4 and P-gp should only be considered if the potential benefits justify the increased risk of bleeding complications.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159200/', '', 'Risdiplam, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Hydroxychloroquine, More', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyri