INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159064, 'Terfenadine', 'Quinine', 'Major', 'Coadministration of a single 430 mg dose of quinine increased plasma concentrations of astemizole and its metabolite, desmethylastemizole. The mechanism is inhibition of hepatic isoenzyme metabolism of astemizole. Elevated levels of astemizole and desmethylastemizole may cause a prolongation of the electrocardiographic QT interval and potentially fatal ventricular arrhythmias.', 'DDInter', 'The concomitant use of quinine and astemizole or terfenadine is considered contraindicated.', 'Metabolism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159236/', '', 'Ketotifen, Mepyramine, Levocetirizine, Chlorcyclizine, Cetirizine, Acrivastine, Triprolidine, Tripelennamine, Azelastine, Dexbrompheniramine, Clemastine, More', 'Risdiplam, Collagenase clostridium histolyticum, Onasemnogene abeparvovec, Hyaluronic acid, Artesunate, Artemether, Pyrimethamine, Tafenoquine, Atovaquone, Proguanil, Sulfadoxine', 1767369485);
