INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159279, 'Acetohexamide', 'Ramipril', 'Moderate', 'The hypoglycemic effect of insulin secretagogues (e.g., sulfonylureas, meglitinides) may be potentiated by certain drugs, including ACE inhibitors, 4-aminoquinolines, amylin analogs, anabolic steroids, fibrates, monoamine oxidase inhibitors (MAOIs, including linezolid), nonsteroidal anti-inflammatory drugs (NSAIDs), salicylates, selective serotonin reuptake inhibitors (SSRIs), sulfonamides, disopyramide, propoxyphene, quinine, quinidine, and ginseng. These drugs may increase the risk of hypoglycemia by enhancing insulin sensitivity (ACE inhibitors, fibrates, ginseng); stimulating insulin secretion (salicylates, NSAIDs, disopyramide, quinine, quinidine, MAOIs, ginseng); decreasing insulin clearance and resistance (4-aminoquinolines); increasing peripheral glucose utilization (SSRIs, insulin-like growth factor); inhibiting gluconeogenesis (SSRIs, MAOIs, insulin-like growth factor); slowing the rate of gastric emptying (amylin analogs); and/or suppressing postprandial glucagon secretion (amylin analogs). Or, they may increase plasma concentration of insulin secretagogues by displacing them from plasma protein binding sites and/or inhibiting their metabolism (fibrates, NSAIDs, salicylates, sulfonamides).', 'DDInter', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment. The oral antidiabetic dosage(s) may require adjustment if an interaction is suspected. Patients should be apprised of the signs and symptoms of hypoglycemia (e.g., headache, dizziness, drowsiness, nausea, hunger, tremor, weakness, sweating, palpitations), how to treat it, and to contact their doctor if it occurs. Patients should be observed for loss of glycemic control when these drugs are withdrawn.', 'Synergism', 'Close monitoring for the development of hypoglycemia is recommended if these drugs are coadministered with insulin secretagogues, particularly in patients with advanced age and/or renal impairment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159451/', '', 'Felodipine, Amlodipine, Verapamil, Amlodipine, Valsartan, Simvastatin, Atorvastatin, Lovastatin, Rosuvastatin, Pravastatin, Ezetimibe, Bempedoic acid', 'Lixisenatide, Albiglutide, Guar gum, Pramlintide, Liraglutide, Troglitazone, Semaglutide, Dulaglutide, Miglitol', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159280, 'Aluminum hydroxide', 'Ramipril', 'Minor', 'Coadministration with antacids may decrease the oral bioavailability of captopril and other angiotensin converting enzyme (ACE) inhibitors due to delayed gastric emptying and/or elevated gastric pH. Based on available data, the clinical significance of this interaction appears to be minor. As a precaution, patients may want to consider separating the administration times of ACE inhibitors and antacids or oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution) by 1 to 2 hours.', 'DDInter', '-', 'Absorption', '', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159452/', '', 'Felodipine, Amlodipine, Verapamil, Hydrochlorothiazide, Indapamide, Amlodipine, Valsartan, Simvastatin, Lovastatin, Niacin, Pravastatin, More', '', 1767369485);
