INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159693, 'Bromfenac', 'Sirolimus', 'Major', 'Coadministration of macrolide immunosuppressants with other nephrotoxic agents may increase the risk and/or severity of renal impairment due to additive adverse effects on the kidney. Clinical experience in coadministration with cyclosporine indicates increased renal toxicity as evidenced by increased serum creatinine and decreased glomerular filtration rate. An interaction with ibuprofen resulting in acute renal failure was suspected in two liver transplant patients who had been stabilized on tacrolimus.', 'DDInter', 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; cyclosporine; deferasirox; gallium nitrate; lithium; mesalamine; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Renal function should be closely monitored both during and after discontinuation of therapy. Patients should be advised to seek medical attention if they experience symptoms that may indicate nephrotoxicity such as decreased urine output, sudden weight gain, fluid retention, edema, or shortness of breath.', 'Synergism', 'Caution is advised when macrolide immunosuppressants is used in patients who have recently received or are receiving treatment with other potentially nephrotoxic drugs (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159866/', '', 'Ascorbic acid, Cyclosporine, Vitamin A, Acetylcysteine, Nandrolone, Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, More', 'Fluocinolone acetonide, Salicylic acid, Fluorometholone', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159694, 'Butabarbital', 'Sirolimus', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus and tacrolimus, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers. Sirolimus and tacrolimus blood levels should be checked frequently and the dosage adjusted accordingly whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159867/', '', 'Suvorexant, Methohexital, Dichloralphenazone, Valerian, Chloral hydrate, Paraldehyde, Ramelteon, Ethchlorvynol, Zopiclone, Remimazolam, Nitrazepam, More', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159695, 'Candida albicans', 'Sirolimus', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159868/', '', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159696, 'Cerivastatin', 'Sirolimus', 'Moderate', 'Coadministration of a macrolide immunosuppressant with certain HMG-CoA reductase inhibitors may result in elevated plasma concentrations of both due to competitive inhibition of CYP450 3A4 metabolism. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity including rhabdomyolysis, which can be fatal.', 'DDInter', 'In general, lovastatin, red yeast rice (which contains lovastatin), and simvastatin should preferably be avoided in patients treated with sirolimus or tacrolimus due to the potential for serious interaction. Atorvastatin may be used with caution, although the dosage should start low, and immunosuppressant blood levels should be closely monitored. Pravastatin and fluvastatin are probably the safest alternatives, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'In general, lovastatin, red yeast rice (which contains lovastatin), and simvastatin should preferably be avoided in patients treated with sirolimus or tacrolimus due to the potential for serious interaction.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159869/', '', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Bempedoic acid, Alirocumab', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Anifrolumab, Canakinumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159697, 'Vibrio cholerae CVD 103-HgR strain live antigen', 'Sirolimus', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159870/', '', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159698, 'Cisapride', 'Sirolimus', 'Moderate', 'Coadministration with gastrointestinal prokinetic agents such as cisapride, domperidone, and metoclopramide may increase the oral bioavailability of macrolide immunosuppressants. Because macrolide immunosuppressants are incompletely and variably absorbed following oral administration, increased gastrointestinal motility and gastric emptying can enhance delivery of the drugs to the small intestine where they are absorbed.', 'DDInter', 'Pharmacologic response, renal function, and macrolide immunosuppressant blood levels should be monitored more closely whenever a gastrointestinal prokinetic agent is added to or discontinue from therapy, and the immunosuppressant dosage adjusted as necessary. Patients should be advised to contact their physician if they experience potential signs and symptoms of macrolide immunosuppressant toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, or lethargy.', 'Absorption', 'Pharmacologic response, renal function, and macrolide immunosuppressant blood levels should be monitored more closely whenever a gastrointestinal prokinetic agent is added to or discontinue from therapy, and the immunosuppressant dosage adjusted as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159871/', '', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, More', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159699, 'Clofibrate', 'Sirolimus', 'Moderate', 'Higher dosages of lipid-lowering agents may be required during concomitant administration of sirolimus or temsirolimus. Dose-related serum lipid abnormalities, including but not limited to increased serum cholesterol and triglycerides, have been frequently reported in patients treated with sirolimus or temsirolimus.', 'DDInter', 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents. Modification of the antihyperlipidemic regimen or dosages may be necessary.', 'Others', 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159872/', '', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Bempedoic acid, Alirocumab', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Heparin, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (159700, 'Clotrimazole', 'Sirolimus', 'Major', 'Coadministration with clotrimazole may significantly increase the oral bioavailability of sirolimus and tacrolimus. The proposed mechanism is clotrimazole inhibition of sirolimus and tacrolimus metabolism via intestinal CYP450 3A4.', 'DDInter', 'Caution is advised if sirolimus or tacrolimus is prescribed with clotrimazole. Immunosuppressant blood levels should be closely monitored and the dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of clotrimazole in patients who are stabilized on their antirejection regimen.', 'Metabolism', 'Caution is advised if sirolimus or tacrolimus is prescribed with clotrimazole.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/159873/', '', 'Salicylic acid, Selenium Sulfide, Thiabendazole, Ciclopirox, Terbinafine, Econazole, Tioconazole, Chlorphenesin, Tetracycline, Epinephrine, Minocycline, More', 'Alteplase, Ascorbic acid, Vitamin A, Potassium Iodide, Acetylcysteine, Nandrolone, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, More', 1767369485);
