INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (160955, 'Amprenavir', 'Ribociclib', 'Major', 'Coadministration with potent CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of ribociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leucopenia, anemia, thrombocytopenia, nausea, vomiting, diarrhea, stomatitis, anorexia, alopecia, fatigue, headache, and abnormal liver function may be increased.', 'DDInter', 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, the dose of ribociclib should be reduced to 400 mg once daily. Patients receiving concomitant treatment with a CYP450 3A4 inhibitor should be monitored for the development of ribociclib-related adverse effects. Following discontinuation of the potent CYP450 3A4 inhibitor, the ribociclib dosage should be returned (after at least 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', 'Concomitant use of ribociclib with potent CYP450 3A4 inhibitors should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161128/', '', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Dolutegravir, Oseltamivir, Acyclovir, More', 'Futibatinib, Tucatinib, Alpelisib, Lenvatinib, Trametinib, Nintedanib, Vandetanib, Alectinib, Dacomitinib, Tivozanib, Sorafenib', 1767369485);
