INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (161396, 'Vemurafenib', 'Rifampicin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of vemurafenib, which has been shown in vitro to be a substrate of the isoenzyme.', 'DDInter', 'Concomitant use of vemurafenib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with vemurafenib. If coadministration is required, the manufacturer recommends increasing the dose of vemurafenib by 240 mg (one tablet) as tolerated. Close monitoring for toxicities (e.g., QT prolongation, hepatotoxicity, nephrotoxicity, photosensitivity, dermatologic reactions, uveitis) is advised following dose increase. Two weeks after discontinuation of the strong CYP450 3A4 inducer, the vemurafenib dose that was taken prior to initiating the inducer may be resumed.', 'Metabolism', 'Concomitant use of vemurafenib with potent CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161569/', '', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Ascorbic acid, Hydroflumethiazide, Chlorothiazide, Sulfadiazine, Lactic acid, Methazolamide, Trichlormethiazide, Ciclopirox, Tioconazole, Brinzolamide, Clotrimazole, More', 1767369485);
