INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (161599, 'Candida albicans', 'Rilonacept', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', '', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161772/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (161600, 'Vibrio cholerae CVD 103-HgR strain live antigen', 'Rilonacept', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161773/', '', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', '', 1767369485);
