INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (161912, 'Gilteritinib', 'Rivaroxaban', 'Moderate', 'Coadministration with gilteritinib may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the intestinal P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), and/or organic cation transporter 1 (OCT1) transporters, such as dabigatran, digoxin, rosuvastatin, methotrexate, and metformin.', 'DDInter', 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever gilteritinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Until more information is available, caution is advised if gilteritinib is used concomitantly with drugs that are substrates of the intestinal P-gp, BCRP, and/or OCT1 transport proteins, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162085/', '', 'Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Caplacizumab, Argatroban, Prasugrel, More', 'Dacomitinib, Bosutinib, Vandetanib, Cobimetinib, Afatinib, Axitinib, Ripretinib, Lenvatinib, Erlotinib, Osimertinib, Infigratinib, More', 1767369485);
