INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162700, 'Rosiglitazone', 'Ketoconazole', 'Moderate', 'Coadministration with ketoconazole may increase the plasma concentrations of rosiglitazone. The proposed mechanism is ketoconazole inhibition of the metabolism of rosiglitazone via CYP450 2C8 and 2C9.', 'DDInter', 'Because the antidiabetic effect of rosiglitazone is dose- and concentration-dependent, pharmacologic response to rosiglitazone should be monitored more closely following the addition or discontinuation of ketoconazole. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations. The rosiglitazone dosage may require adjustment if an interaction is suspected.', 'Metabolism', 'Because the antidiabetic effect of rosiglitazone is dose- and concentration-dependent, pharmacologic response to rosiglitazone should be monitored more closely following the addition or discontinuation of ketoconazole.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162873/', '', 'Glipizide, Acetohexamide, Guar gum, Pramlintide, Alogliptin, Tolazamide, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Glimepiride, More', 'Tinidazole, Ascorbic acid, Sulfadiazine, Lactic acid, Amphotericin B, Ciclopirox, Tioconazole, Brinzolamide, Furazolidone, Diiodohydroxyquinoline, Oxytetracycline, More', 1767369485);
