INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162782, 'Fostemsavir', 'Rosuvastatin', 'Moderate', 'Coadministration with fostemsavir may increase the plasma concentrations of most HMG-CoA reductase inhibitors (i.e., statins) to varying degrees. The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1/1B3-mediated hepatic uptake by temsavir, the active moiety of fostemsavir. Inhibition of breast cancer resistance protein (BCRP)-mediated intestinal and hepatic transport of statins may also contribute.', 'DDInter', 'The lowest effective dosage of a statin should be used when prescribed with fostemsavir, and the dosage titrated cautiously based on clinical response and adverse events. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by fever, malaise, and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'The lowest effective dosage of a statin should be used when prescribed with fostemsavir, and the dosage titrated cautiously based on clinical response and adverse events.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162955/', '', 'Amlodipine, Ramipril, Lisinopril, Perindopril, Acetylsalicylic acid, Niacin, Bempedoic acid, Indapamide, Fenofibrate, Gemfibrozil, Fenofibric acid, More', 'Paritaprevir, Tenofovir alafenamide, Cabotegravir, Maraviroc, Zanamivir, Lenacapavir, Telaprevir, Rilpivirine, Dolutegravir, Brincidofovir, Elbasvir, More', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162783, 'Gemfibrozil', 'Rosuvastatin', 'Major', 'Coadministration with gemfibrozil may significantly increase the plasma concentrations of rosuvastatin. The proposed mechanism is gemfibrozil inhibition of the hepatic uptake of rosuvastatin via organic anion transporting polypeptide 2 (OATP-2).', 'DDInter', 'Concurrent use of rosuvastatin and gemfibrozil should generally be avoided unless the benefit of further alterations in lipid levels is anticipated to outweigh the potential risks. Addition of fibrates to HMG-CoA reductase inhibitor therapy typically provides little additional reduction in LDL cholesterol, but further reductions of triglycerides and increases in HDL cholesterol may be attained. The daily dosage of rosuvastatin should not exceed 10 mg when used in combination with gemfibrozil. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Concurrent use of rosuvastatin and gemfibrozil should generally be avoided unless the benefit of further alterations in lipid levels is anticipated to outweigh the potential risks.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162956/', '', 'Indapamide, Bempedoic acid, Perindopril, Gemfibrozil, Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, More', 'Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Cholestyramine, Rosuvastatin, Colestipol, Colesevelam, Alirocumab', 1767369485);
