INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162897, 'Thalidomide', 'Rotigotine', 'Moderate', 'Thalidomide can commonly cause drowsiness/sedation and may have additive effects with other drugs that cause central nervous system (CNS) depression.', 'DDInter', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics. Patients should be advised to avoid hazardous activities requiring mental alertness and motor coordination until they know how these agents affect them, and to notify their physician if they experience excessive or prolonged CNS effects that interfere with their normal activities. Dosage reductions may be necessary. Thalidomide should preferably be administered at bedtime to reduce the impact of somnolence. Alcohol use is not recommended.', 'Synergism', 'Caution is advised when thalidomide is used with other CNS depressants such as anticonvulsants, antihistamines, antidepressants, antipsychotics, anxiolytics, opioids, and sedatives/hypnotics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163070/', '', 'Golimumab, Cyclosporine, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, More', 'Safinamide, Amantadine, Selegiline, Cabergoline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (162898, 'Thiethylperazine', 'Rotigotine', 'Moderate', 'Agents with central antidopaminergic activity such as phenothiazines, neuroleptics, and certain antiemetics may antagonize the pharmacologic effects of dopaminergic drugs, and vice versa. In addition, the central nervous system depressant and hypotensive effects of these agents may be additively or synergistically increased when taken together.', 'DDInter', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided. If coadministration is necessary, patients should be alerted to the possibility of excessive drowsiness and monitored for potentially diminished therapeutic response to both treatments. Patients treated for Parkinson''s disease should generally avoid antidopaminergic agents, particularly phenothiazines and older neuroleptic agents, since these agents may cause extrapyramidal reactions and exacerbate the symptoms of Parkinson''s disease. Likewise, patients with a major psychotic disorder should ordinarily not be treated with dopaminergic drugs because of the risk of exacerbating the psychosis with an increase in central dopaminergic tone.', 'Synergism', 'Concomitant use of dopaminergic drugs with antidopaminergic agents should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163071/', '', 'Ketotifen, Fexofenadine, Loratadine, Desloratadine, Azelastine, Terfenadine, Astemizole', '', 1767369485);
