INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163490, 'Zanubrutinib', 'Ruxolitinib', 'Major', 'Coadministration of zanubrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Discontinue zanubrutinib if intracranial hemorrhage of any grade occurs.', 'Synergism', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163663/', '', 'Dacomitinib, Tepotinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Zanubrutinib, Axitinib, More', 'Dacomitinib, Tepotinib, Bosutinib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Afatinib, Tivozanib, Brigatinib, Encorafenib, More', 1767369485);
