INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163609, 'Irinotecan (liposomal)', 'Sacituzumab govitecan', 'Major', 'Coadministration of sacituzumab govitecan with irinotecan may increase the risk of adverse effects, such as severe neutropenia, nausea, vomiting, and diarrhea. The proposed mechanism involves increased exposure to SN-38, a potent topoisomerase I inhibitor. SN-38 is the pharmacologically active metabolite of irinotecan, and considered to be approximately 1000 times more potent than irinotecan. However, SN-38 is also delivered to Trop-2-expressing cancer cells via the Trop-2-directed antibody drug conjugate sacituzumab govitecan-hziy. SN-38 is covalently attached to sacituzumab govitecan, which binds to Trop-2-expressing cancer cells and allows for internalization of SN-38 into these cells to interact with topoisomerase I.', 'DDInter', 'Sacituzumab govitecan should not be substituted for or used with other drugs containing irinotecan or the active metabolite SN-38.', 'Synergism', '', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163782/', '', 'Trabectedin, Cabazitaxel, Vinblastine, Paclitaxel, Vincristine, Teniposide, Vinorelbine, Etoposide, Docetaxel', 'Pertuzumab, Cetuximab, Avelumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, Pembrolizumab, More', 1767369485);
