INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163638, 'Ergotamine', 'Safinamide', 'Major', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs) may potentiate the pharmacologic activity of serotonergic agents such as serotonin reuptake inhibitors, 5-HT1 receptor agonists, ergot alkaloids, buspirone, dextromethorphan, and most antidepressants. The result may be an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with serotonergic agents.', 'Synergism', 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163811/', '', 'Frovatriptan, Eptinezumab, Atogepant, Eletriptan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Methysergide, More', 'Apomorphine, Ropinirole, Opicapone, Cabergoline, Entacapone, Tolcapone, Rotigotine, Pergolide, Pramipexole, Amantadine, Levodopa', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163639, 'Escitalopram', 'Safinamide', 'Moderate', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs), including the selective MAO-B inhibitor safinamide, may potentiate the pharmacologic activity of serotonergic agents such as the serotonin reuptake inhibitors (SSRIs). The result may be an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucinations, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', 'DDInter', 'Caution is advised if safinamide is used concomitantly with SSRIs and the SSRI should be administered at the lowest effective dose. Clinical and laboratory monitoring should be considered when these drugs are used concomitantly.', 'Synergism', 'Caution is advised if safinamide is used concomitantly with SSRIs and the SSRI should be administered at the lowest effective dose.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163812/', '', 'Amantadine, Cabergoline', '', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163640, 'Esketamine', 'Safinamide', 'Major', 'Coadministration with psychostimulants (e.g., amphetamines) or monoamine oxidase inhibitors (MAOIs) may potentiate the hypertensive effects of esketamine.', 'DDInter', 'Caution is advised and blood pressure should be closely monitored during concomitant use of esketamine with psychostimulants or MAOIs. All patients receiving esketamine should have BP assessed prior to administration. If BP is elevated (e.g., >140 mmHg systolic, >90 mmHg diastolic), a delay in esketamine treatment may be necessary, taking into consideration the benefits versus risks in individual patients. BP should be monitored for at least 2 hours after esketamine administration, starting at approximately 40 minutes post-dose and subsequently as clinically warranted. In patients with a history of hypertensive encephalopathy, more intensive monitoring is warranted due to increased risk for developing encephalopathy with even small increases in BP. If at any point BP is elevated and remains high, promptly seek assistance from practitioners experienced in BP management. Patients experiencing symptoms of a hypertensive crisis (e.g., chest pain, shortness of breath) or hypertensive encephalopathy (e.g., sudden severe headache, visual disturbances, seizures, diminished consciousness or focal neurological deficits) should be immediately referred for emergency care.', 'Synergism', 'Caution is advised and blood pressure should be closely monitored during concomitant use of esketamine with psychostimulants or MAOIs.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163813/', '', 'Halothane, Sodium oxybate, Isoflurane, Nitrous oxide, Desflurane, Propofol, Enflurane, Sevoflurane, Etomidate', '', 1767369485);
