INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163801, 'Histrelin', 'Salmeterol', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163974/', '', 'Mometasone furoate, Umeclidinium, Budesonide, Glycopyrronium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Tiotropium, Beclomethasone dipropionate', 'Megestrol acetate, Ethinylestradiol, Diethylstilbestrol, Medroxyprogesterone acetate', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (163802, 'Hydroflumethiazide', 'Salmeterol', 'Moderate', 'Coadministration with beta-2 adrenergic agonists may potentiate the hypokalemic effects of potassium-wasting diuretics. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations. Since QT prolongation is a possible side effect of beta-2 agonists, exacerbation of hypokalemia may increase the risk of torsade de pointes and other serious arrhythmias. The interaction may be more likely with systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or concomitant theophylline or corticosteroid therapy.', 'DDInter', 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics. Serum potassium level and cardiovascular status should be monitored, especially if the beta-2 agonist is administered systemically or by nebulizer. Patients should be advised to notify their physician if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163975/', '', 'Tinidazole, Ascorbic acid, Simeprevir, Lactic acid, Mitapivat, Amphotericin B, Ciclopirox, Tioconazole, Brinzolamide, Diiodohydroxyquinoline, Oxytetracycline, More', 'Mometasone furoate, Umeclidinium, Aclidinium, Mometasone, Epinephrine, Fluticasone, Cromoglicic acid, Ipratropium, Bitolterol, Isoprenaline, Tiotropium', 1767369485);
