INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164122, 'Tretinoin', 'Sarecycline', 'Major', 'Concomitant use of vitamin A or related compounds (i.e., retinoids) with tetracyclines may increase the risk of pseudotumor cerebri, also known as benign intracranial hypertension. These agents individually have been associated with the development of pseudotumor cerebri and may have additive effects during coadministration. The interaction has been suspected in reported cases involving the use of isotretinoin or vitamin A in combination with a tetracycline.', 'DDInter', 'Coadministration of most retinoids with tetracyclines is considered contraindicated. Patients treated with a retinoid or a tetracycline should be advised to discontinue the medication and notify their physician immediately if they develop early symptoms of pseudotumor cerebri such as headache, nausea, vomiting, visual disturbances, vision loss, and papilledema. Although intracranial hypertension typically resolves after discontinuation of treatment, the possibility for permanent visual loss exists. Therefore, prompt ophthalmologic evaluation should be performed if visual disturbances occur. Since intracranial pressure may remain elevated for weeks after drug cessation, patients should be monitored until they stabilize.', 'Synergism', 'Coadministration of most retinoids with tetracyclines is considered contraindicated.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164295/', '', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, More', 'Tigecycline', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164123, 'Typhoid vaccine (live)', 'Sarecycline', 'Major', 'INTERVAL: The use of live, attenuated oral typhoid vaccine with antibacterial sulfonamides or other antibiotics may result in a diminished immunologic response to the vaccine. Some antibiotics may be active against the vaccine strain of Salmonella typhi (Ty21a), thereby preventing a sufficient degree of multiplication to occur in order to induce a protective immune response.', 'DDInter', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics. A longer interval should be considered following treatment with long-acting antibiotics (e.g., azithromycin). If malaria prophylaxis is needed, the same 3-day interval at the minimum should be observed between antimalarials and the vaccine. However, chloroquine, mefloquine, and pyrimethamine-sulfadoxine may be given concomitantly with the vaccine. Alternatively, parenteral typhoid vaccine (typhoid Vi polysaccharide vaccine) may be considered if coadministration with antibacterial agents is required.', 'Others', 'Live, attenuated oral typhoid vaccine should not be administered during and for at least 3 days before and after treatment with antibacterial sulfonamides or other antibiotics.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164296/', '', '', '', 1767369485);
