INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (164500, 'Everolimus', 'Secobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the blood concentrations and pharmacologic effects of everolimus.', 'DDInter', 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine. If concomitant use is required, a dose adjustment of everolimus should be considered to achieve the recommended therapeutic range for the condition being treated. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase and 2 weeks after discontinuation of the inducer.', 'Metabolism', 'Some manufacturers recommend avoiding concomitant use of everolimus with moderate CYP450 3A4 and/or P-gp inducers such as bosentan, efavirenz, and nevirapine.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164673/', '', 'Triazolam, Flurazepam, Methohexital, Zolpidem, Zaleplon, Dichloralphenazone, Valerian, Temazepam, Chloral hydrate, Quazepam, Paraldehyde, More', 'Pralsetinib, Dabrafenib, Asciminib, Dacomitinib, Pexidartinib, Pacritinib, Fedratinib, Vandetanib, Afatinib, Tivozanib, Lorlatinib, More', 1767369485);
